package cc.surui.app.data;import java.util.List;import cc.surui.model.Keyword;import cc.surui.model.Product;import cc.surui.model.Ranking;import cc.surui.model.Store;public class DataCenter {	public List<Product> products;	public List<Store> stores;	public List<Keyword> keywords;	public List<Ranking> rankings;	public static DataCenter dataCenter;	public static DataCenter instance() {		dataCenter = dataCenter == null ? new DataCenter() : dataCenter;		return dataCenter;	}}