package cc.surui.reptile;public class AppleReptile {	public static void main(String[] args) {		String letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890";		for(int i = 0; i < letters.length(); i++){			char letterI = letters.charAt(i);			for(int j = 0; j < letters.length(); j++){				char letterJ = letters.charAt(j);				String partNumber = String.valueOf(letterI) + String.valueOf(letterJ);				HtmlInfo htmlInfo = HtmlInfo.create("http://store.apple.com/hk-zh/buy-iphone/iphone6?cppart=UNLOCKED/WW&product=MG%s2ZP/A&step=accessories#", partNumber);				if(htmlInfo == null) continue;				String partName = htmlInfo.getPrartName();				System.out.println("MG" + partNumber + "2ZP" +"\t" + partName);			}		}	}}